mkdir "LSD Dream Emulator" && cd "LSD Dream Emulator" && npm init -y && echo 'const { app, BrowserWindow } = require("electron");function createWindow() {const win = new BrowserWindow({width: 1200,height: 900,frame: false,resizable: false,webPreferences: {nodeIntegration: true}});win.loadFile("index.html");}app.whenReady().then(createWindow);app.on("window-all-closed", () => {if (process.platform !== "darwin") {app.quit();}});app.on("activate", () => {if (BrowserWindow.getAllWindows().length === 0) {createWindow();}});' > index.js && echo '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>LSD Dream Emulator</title><style>body{margin:0;overflow:hidden}iframe{width:1200px;height:900px}</style></head><body><iframe src="https://www.retrogames.cc/embed/41792-lsd-dream-emulator.html" frameborder="no" allowfullscreen="true" webkitallowfullscreen="true" mozallowfullscreen="true" scrolling="no"></iframe></body></html>' > index.html && npm install electron && echo 'npx electron .' > run.sh && chmod +x run.sh
